CREATE TABLE IF NOT EXISTS "userinfo"."userinfo"(id INTEGER PRIMARY KEY, first_name VARCHAR(255), last_name VARCHAR(255), profile_image VARCHAR(255), created_at DATE);

CREATE TABLE IF NOT EXISTS "userinfo"."places"(id SERIAL PRIMARY KEY, name VARCHAR(255), user_id INTEGER, address_id INTEGER, CONSTRAINT fk_user_id FOREIGN KEY(user_id) REFERENCES "userinfo"."userinfo"(id) ON DELETE CASCADE);

CREATE TABLE IF NOT EXISTS "userinfo"."report"(id SERIAL PRIMARY KEY, user_id INTEGER, reported_id INTEGER, title VARCHAR(255), category VARCHAR(255), description VARCHAR(1024), CONSTRAINT fk_user_id FOREIGN KEY(user_id) REFERENCES "userinfo"."userinfo"(id) ON DELETE CASCADE, CONSTRAINT fk_reported_id FOREIGN KEY(reported_id) REFERENCES "userinfo"."userinfo"(id) ON DELETE CASCADE);

CREATE TABLE IF NOT EXISTS "userinfo"."license"(id SERIAL PRIMARY KEY, user_id INTEGER UNIQUE, license_number VARCHAR, first_name VARCHAR, middle_name VARCHAR, last_name VARCHAR, sex VARCHAR, height VARCHAR, weight VARCHAR, dob VARCHAR, expiry VARCHAR, issued VARCHAR, address1 VARCHAR, address2 VARCHAR, postcode VARCHAR, issuer_region VARCHAR, issuer_org VARCHAR, verification BOOLEAN, CONSTRAINT fk_user_doc_id FOREIGN KEY(user_id) REFERENCES "userinfo"."userinfo"(id) ON DELETE CASCADE);